INSERT INTO procedure_reviews (user_id, category, procedure_name, hospital_name, cost, procedure_rating, hospital_rating, gender, age_group, surgery_date, content, images, created_at) VALUES
  (1001, '눈성형', '부분절개 쌍꺼풀', '강남 아이성형외과', 180, 5, 4, '여', '20대', '2024-11-15', '처음엔 붓기가 생각보다 있어서 걱정했는데\n3주 지나니까 라인이 자연스럽게 자리 잡았어요.\n\n화장 안 해도 눈이 또렷해 보여서 만족 중이고\n아직 잔붓기는 조금 남아 있는 상태예요.\n시간 지나면 더 자연스러워질 것 같아요.', NULL, '2024-12-10T10:23:45Z'),
  (1002, '눈성형', '눈매교정', '청담 라미성형외과', 250, 4, 5, '여', '30대', NULL, '졸린 눈 때문에 상담만 다녀왔어요.\n\n과한 교정은 필요 없다고 하셔서\n부분 교정만 추천받았고\n설명도 이해하기 쉽게 해주셨어요.\n\n여러 군데 더 상담해보고 결정하려고요.', NULL, '2024-12-09T14:12:30Z'),
  (1003, '코', '코 필러', '압구정 뷰티클리닉', 120, 4, 4, '여', '20대', '2024-11-28', '큰 변화보다는\n인상이 정리된 느낌이에요.\n\n시술은 금방 끝났고\n멍은 거의 없었어요.\n지금은 붓기 빠지면서 점점 자연스러워지고 있어요.', NULL, '2024-12-08T16:45:20Z'),
  (1004, '코', '코 수술', '신사동 프리미어성형외과', 450, 5, 5, '여', '30대', '2024-10-20', '수술 후 1주차까지는 숨 쉬는 것도 불편했고\n붓기 때문에 거울 보기 싫었어요.\n\n2주 지나니까 많이 가라앉았고\n한 달 차 되니까 이제 좀 사람 얼굴 된 느낌이에요.\n급하게 판단하지 말고 기다리는 게 중요한 것 같아요.', NULL, '2024-12-07T09:30:15Z'),
  (1005, '윤곽', '윤곽 주사', '홍대 더블유클리닉', 200, 4, 4, '여', '20대', '2024-11-20', '드라마틱한 변화는 아니고\n턱선이 정리되는 느낌 정도예요.\n\n다운타임 거의 없어서\n회사 다니면서 받기엔 괜찮았어요.\n여러 번 나눠서 받는 게 맞는 것 같아요.', NULL, '2024-12-06T11:20:40Z'),
  (1006, '윤곽', '턱 보톡스', '이태원 뷰티라인', 150, 5, 4, '여', '30대', '2024-11-10', '처음엔 변화가 없어서\n괜히 했나 싶었는데\n2주 지나니까 확실히 갸름해졌어요.\n\n씹을 때 불편함은 하루 이틀 정도 있었고\n지금은 거의 없어요.', NULL, '2024-12-05T13:15:25Z'),
  (1007, '리프팅', '인모드 리프팅', '강남 스킨앤성형외과', 350, 4, 5, '여', '40대', '2024-11-05', '즉각적인 변화는 크지 않았고\n며칠 지나면서 탄력이 느껴졌어요.\n\n통증은 생각보다 있었지만\n참을 만한 정도였고\n피부 컨디션은 확실히 좋아진 느낌이에요.', NULL, '2024-12-04T15:40:10Z'),
  (1008, '레이저', '레이저 토닝', '청담 피부과', 80, 3, 4, '여', '30대', '2024-11-25', '기미 개선 목적이었는데\n한 번으로는 큰 변화는 없었어요.\n\n다만 피부톤이 맑아진 느낌은 있어서\n꾸준히 받아보려고 해요.', NULL, '2024-12-03T10:55:30Z'),
  (1009, '지방', '복부 지방분해주사', '압구정 바디클리닉', 180, 4, 4, '여', '30대', '2024-11-18', '시술 후 바로 변화는 없고\n2주 정도 지나니까 옷이 조금 헐렁해졌어요.\n\n멍은 꽤 있었고\n운동 병행하니까 효과가 더 있는 것 같아요.', NULL, '2024-12-02T12:30:45Z'),
  (1010, '체형', '허벅지 시술', '강남 바디라인성형외과', 320, 5, 5, '여', '20대', '2024-11-12', '앉을 때 뻐근함이 며칠 있었지만\n일상생활은 가능했어요.\n\n라인 정리 목적이라\n큰 사이즈 변화보다는\n실루엣이 예뻐진 느낌이에요.', NULL, '2024-12-01T14:20:15Z'),
  (1011, '눈성형', '전절개 쌍꺼풀', '강남 프리미엄성형외과', 220, 5, 5, '여', '20대', '2024-10-30', '2개월 지났는데 완전 만족이에요!\n처음엔 붓기가 심해서 걱정했는데\n지금은 완전 자연스럽고 예뻐요.\n\n병원 분위기도 좋고\n의사 선생님 설명도 친절하셨어요.', NULL, '2024-11-30T16:10:30Z'),
  (1012, '코', '콧대 필러', '청담 뷰티플러스', 100, 4, 4, '남', '30대', '2024-11-22', '간단한 시술이라 부담 없이 받았어요.\n결과도 자연스럽고 만족합니다.\n다만 효과가 영구적이 아니라서\n나중에 다시 받아야 할 것 같아요.', NULL, '2024-11-29T11:45:20Z'),
  (1013, '보톡스', '이마 보톡스', '압구정 스킨클리닉', 90, 5, 4, '여', '30대', '2024-11-15', '주름 개선 목적으로 받았는데\n2주 지나니까 확실히 효과가 보여요.\n\n통증도 거의 없었고\n다운타임도 전혀 없어서 좋았어요.', NULL, '2024-11-28T09:20:15Z'),
  (1014, '윤곽', '턱끝 보톡스', '강남 라인성형외과', 130, 4, 4, '여', '20대', '2024-11-08', '각진 턱선 때문에 받았어요.\n한 달 정도 지나니까\n확실히 갸름해진 느낌이에요.\n\n씹기 불편함은 일주일 정도 있었지만\n지금은 전혀 문제없어요.', NULL, '2024-11-27T13:30:40Z'),
  (1015, '리프팅', '실 리프팅', '청담 에이스성형외과', 280, 4, 5, '여', '40대', '2024-10-25', '처음엔 실이 만져져서 신경 쓰였는데\n지금은 거의 느껴지지 않아요.\n\n리프팅 효과는 확실히 있고\n피부 탄력도 좋아진 것 같아요.', NULL, '2024-11-26T15:15:25Z'),
  (2847, '눈성형', '무절개 쌍꺼풀', '강남 뷰티메드', 160, 5, 4, '여', '20대', '2024-11-08', '무절개라 다운타임이 거의 없어서 좋았어요.\n3일 정도만 붓기가 있었고\n지금은 완전 자연스러워요.\n\n회사 다니면서 받기 좋은 시술인 것 같아요.', NULL, '2024-11-25T10:30:20Z'),
  (3921, '눈성형', '상안검 수술', '청담 미라클성형외과', 300, 4, 4, '여', '30대', '2024-10-15', '눈이 무겁고 졸린 느낌이 있어서 받았어요.\n2개월 지났는데 확실히 눈이 밝아졌어요.\n\n회복 기간이 좀 길었지만\n결과는 만족스러워요.', NULL, '2024-11-24T14:20:15Z'),
  (4562, '코', '콧볼 축소', '압구정 코성형외과', 280, 5, 5, '여', '20대', '2024-11-01', '콧볼이 넓어서 고민이었는데\n수술 받고 확실히 작아졌어요.\n\n회복은 좀 오래 걸렸지만\n결과는 완전 만족입니다!', NULL, '2024-11-23T09:15:40Z'),
  (5234, '코', '매부리코 수술', '강남 프리미엄성형외과', 420, 4, 4, '남', '30대', '2024-10-10', '매부리코 때문에 고민이 많았는데\n수술 받고 확실히 개선됐어요.\n\n의사 선생님도 친절하시고\n전체적으로 만족스러운 경험이었습니다.', NULL, '2024-11-22T16:45:30Z'),
  (6789, '윤곽', '사각턱 보톡스', '홍대 뷰티라인', 140, 5, 4, '여', '20대', '2024-11-05', '사각턱 때문에 받았는데\n한 달 지나니까 확실히 갸름해졌어요.\n\n씹기 불편함은 일주일 정도 있었지만\n지금은 전혀 문제없어요.\n만족합니다!', NULL, '2024-11-21T11:30:25Z'),
  (7123, '윤곽', '윤곽 리프팅', '이태원 클리닉', 250, 4, 4, '여', '30대', '2024-10-28', '턱선 정리 목적으로 받았어요.\n효과는 확실히 있지만\n여러 번 받아야 할 것 같아요.\n\n전체적으로는 만족스러워요.', NULL, '2024-11-20T13:20:10Z'),
  (8456, '보톡스', '눈가 보톡스', '강남 스킨앤성형외과', 110, 5, 5, '여', '30대', '2024-11-12', '눈가 주름 때문에 받았어요.\n2주 지나니까 확실히 개선됐어요.\n\n통증도 거의 없었고\n다운타임도 전혀 없어서 좋았어요.', NULL, '2024-11-19T15:10:45Z'),
  (9234, '보톡스', '입꼬리 보톡스', '청담 뷰티플러스', 95, 4, 4, '여', '20대', '2024-11-03', '입꼬리가 내려가서 고민이었는데\n보톡스 받고 확실히 올라갔어요.\n\n자연스럽고 만족스러워요.', NULL, '2024-11-18T10:25:30Z'),
  (10234, '리프팅', '울쎄라 리프팅', '압구정 스킨클리닉', 400, 4, 4, '여', '40대', '2024-10-20', '탄력 개선 목적으로 받았어요.\n통증은 좀 있었지만\n효과는 확실히 있어요.\n\n피부가 더 탄탄해진 느낌이에요.', NULL, '2024-11-17T12:40:20Z'),
  (11245, '레이저', '피코레이저', '강남 피부과', 150, 4, 4, '여', '30대', '2024-11-14', '모공과 잡티 개선 목적으로 받았어요.\n한 번으로는 큰 변화는 없지만\n피부톤이 맑아진 느낌은 있어요.\n\n여러 번 받아봐야 할 것 같아요.', NULL, '2024-11-16T14:15:35Z'),
  (12356, '레이저', '프락셀 레이저', '청담 피부과', 200, 3, 3, '여', '30대', '2024-11-07', '흉터 개선 목적으로 받았는데\n통증이 생각보다 심했어요.\n\n효과는 아직 확실하지 않네요.\n추가 시술 받아봐야 할 것 같아요.', NULL, '2024-11-15T09:50:25Z'),
  (13467, '지방', '팔뚝 지방분해주사', '압구정 바디클리닉', 160, 4, 4, '여', '30대', '2024-11-02', '팔뚝 살 때문에 받았어요.\n2주 지나니까 옷이 조금 헐렁해졌어요.\n\n멍은 좀 있었지만\n전체적으로는 만족스러워요.', NULL, '2024-11-14T11:30:15Z'),
  (14578, '체형', '복부 지방흡입', '강남 바디라인성형외과', 550, 5, 5, '여', '30대', '2024-10-05', '운동으로 안 빠지는 살 때문에 받았어요.\n2개월 지났는데 확실히 효과가 있어요.\n\n회복 기간은 좀 길었지만\n결과는 완전 만족입니다!', NULL, '2024-11-13T13:20:40Z'),
  (15689, '체형', '엉덩이 리프팅', '이태원 바디클리닉', 380, 4, 4, '여', '20대', '2024-10-18', '라인 정리 목적으로 받았어요.\n실루엣이 예뻐진 느낌이에요.\n\n앉을 때 불편함은 며칠 있었지만\n지금은 전혀 문제없어요.', NULL, '2024-11-12T15:45:20Z');
INSERT INTO hospital_reviews (user_id, hospital_name, category_large, procedure_name, visit_date, overall_satisfaction, hospital_kindness, has_translation, translation_satisfaction, content, images, created_at) VALUES
  (2001, '강남 아이성형외과', '눈성형', '쌍꺼풀 수술', '2024-12-05', 5, 5, false, NULL, '여기는 과한 권유가 없어서 좋았어요.\n\n제가 고민하는 부분 위주로 설명해 주셨고\n굳이 안 해도 되는 시술은 빼주셨어요.\n신뢰감은 확실히 있었습니다.', NULL, '2024-12-10T11:30:20Z'),
  (2002, '청담 라미성형외과', '눈성형', NULL, '2024-12-03', 4, 5, true, 5, '상담만 다녀왔는데\n통역 서비스가 정말 좋았어요.\n의사 선생님 말씀을 정확하게 전달해주셔서\n이해하기 쉬웠습니다.\n\n여러 군데 더 상담해보고 결정하려고요.', NULL, '2024-12-09T15:20:10Z'),
  (2003, '압구정 뷰티클리닉', '코', '코 필러', '2024-11-28', 4, 4, false, NULL, '분위기가 깔끔하고 좋았어요.\n직원분들도 친절하시고\n시술 과정도 편안했습니다.\n\n결과도 만족스러워요.', NULL, '2024-12-08T10:15:30Z'),
  (2004, '신사동 프리미어성형외과', '코', '코 수술', '2024-10-20', 5, 5, true, 4, '수술 전 상담부터 회복 관리까지\n정말 꼼꼼하게 봐주셨어요.\n\n통역도 잘 되고\n의사 선생님도 친절하셔서\n안심하고 수술 받을 수 있었어요.', NULL, '2024-12-07T14:40:15Z'),
  (2005, '홍대 더블유클리닉', '윤곽', '윤곽 주사', '2024-11-20', 4, 4, false, NULL, 'A병원은 빠른 진행 위주였고\nB병원은 설명은 자세했지만 부담스러웠어요.\n\n여기는 중간 정도 느낌이라\n결정하게 되면 여기로 갈 것 같아요.', NULL, '2024-12-06T12:25:40Z'),
  (2006, '이태원 뷰티라인', '윤곽', '턱 보톡스', '2024-11-10', 5, 4, false, NULL, '위치가 좋고 접근하기 편해요.\n직원분들도 친절하시고\n시술도 깔끔하게 잘 해주셨어요.\n\n결과도 만족스럽습니다.', NULL, '2024-12-05T16:10:25Z'),
  (2007, '강남 스킨앤성형외과', '리프팅', '인모드 리프팅', '2024-11-05', 4, 5, true, 5, '시설이 깔끔하고 좋았어요.\n의사 선생님 설명도 자세하시고\n통역 서비스도 완벽했습니다.\n\n시술 결과도 만족스러워요.', NULL, '2024-12-04T09:50:30Z'),
  (2008, '청담 피부과', '레이저', '레이저 토닝', '2024-11-25', 3, 4, false, NULL, '시설은 깔끔했는데\n대기 시간이 좀 길었어요.\n\n시술 자체는 괜찮았지만\n효과는 아직 확실하지 않네요.\n추가 시술 받아봐야 할 것 같아요.', NULL, '2024-12-03T11:35:20Z'),
  (2009, '압구정 바디클리닉', '지방', '복부 지방분해주사', '2024-11-18', 4, 4, false, NULL, '바디 시술 전문이라\n설명이 자세하고 좋았어요.\n\n직원분들도 친절하시고\n시술 후 관리도 잘 봐주셨어요.', NULL, '2024-12-02T13:20:45Z'),
  (2010, '강남 바디라인성형외과', '체형', '허벅지 시술', '2024-11-12', 5, 5, true, 5, '여러 병원 상담 후 느낌\n\n여기는 설명이 정말 자세하고\n과한 권유 없이 필요한 것만 추천해주셨어요.\n통역도 완벽하고\n전체적으로 신뢰감이 있었어요.', NULL, '2024-12-01T15:45:10Z'),
  (21034, '강남 뷰티메드', '눈성형', '무절개 쌍꺼풀', '2024-11-08', 5, 4, false, NULL, '상담만 다녀왔는데\n설명이 정말 자세하고 좋았어요.\n\n과한 권유 없이\n제 상황에 맞는 시술만 추천해주셨어요.\n신뢰감이 있었습니다.', NULL, '2024-11-30T10:20:30Z'),
  (22345, '청담 미라클성형외과', '눈성형', '상안검 수술', '2024-10-15', 4, 4, true, 4, '통역 서비스가 있어서\n이해하기 쉬웠어요.\n\n의사 선생님도 친절하시고\n전체적으로 만족스러운 경험이었습니다.', NULL, '2024-11-29T14:15:20Z'),
  (23456, '압구정 코성형외과', '코', '콧볼 축소', '2024-11-01', 5, 5, false, NULL, '코 전문 병원이라\n설명이 정말 자세했어요.\n\n의사 선생님도 친절하시고\n수술 후 관리도 꼼꼼하게 봐주셨어요.', NULL, '2024-11-28T11:30:45Z'),
  (24567, '홍대 뷰티라인', '윤곽', '사각턱 보톡스', '2024-11-05', 5, 4, false, NULL, '위치가 좋고 접근하기 편해요.\n직원분들도 친절하시고\n시술도 깔끔하게 잘 해주셨어요.', NULL, '2024-11-27T09:40:15Z'),
  (25678, '이태원 클리닉', '윤곽', '윤곽 리프팅', '2024-10-28', 4, 4, false, NULL, '시설이 깔끔하고 좋았어요.\n의사 선생님 설명도 자세하시고\n전체적으로 만족스러워요.', NULL, '2024-11-26T13:25:30Z'),
  (26789, '강남 피부과', '레이저', '피코레이저', '2024-11-14', 4, 4, false, NULL, '피부 전문이라\n설명이 자세하고 좋았어요.\n\n직원분들도 친절하시고\n시술 후 관리도 잘 봐주셨어요.', NULL, '2024-11-25T15:10:20Z'),
  (27890, '이태원 바디클리닉', '체형', '엉덩이 리프팅', '2024-10-18', 4, 4, true, 5, '바디 시술 전문이라\n설명이 정말 자세했어요.\n\n통역도 완벽하고\n전체적으로 신뢰감이 있었어요.', NULL, '2024-11-24T10:50:35Z');
INSERT INTO concern_posts (user_id, title, concern_category, content, created_at) VALUES
  (3001, '첫 시술 너무 고민돼요', '시술 고민', '처음이라 정보만 계속 찾아보고 있어요.\n\n후기 보면 다 다른 말이라 더 헷갈리고\n결정이 잘 안 되네요.\n다들 첫 시술 어떻게 결정하셨나요?', '2024-12-10T08:30:15Z'),
  (3002, '회복 기간 멘탈 관리…', '시술 고민', '회복 중엔 거울 보지 말라는 말\n이제야 이해했어요.\n\n지금은 조금씩 나아지고 있어서\n그냥 시간 지나길 기다리는 중입니다.', '2024-12-09T12:20:30Z'),
  (3003, '쌍꺼풀 수술 병원 추천 부탁드려요', '병원 선택', '강남 지역에서 쌍꺼풀 수술 받을 병원 찾고 있어요.\n여러 곳 상담 다녀봤는데\n결정이 어렵네요.\n\n추천해주실 곳 있나요?', '2024-12-08T10:15:45Z'),
  (3004, '코 필러 vs 코 수술 고민', '시술 고민', '코 필러로 시작할지\n아니면 처음부터 수술 받을지\n고민이에요.\n\n필러는 임시적이고\n수술은 부담스러운데...\n어떻게 하시는 분들 많으신가요?', '2024-12-07T14:30:20Z'),
  (3005, '보톡스 부작용 걱정돼요', '시술 고민', '턱 보톡스 받으려고 하는데\n부작용이 걱정이에요.\n\n씹기 불편하다는 얘기 많이 듣는데\n정말 그런가요?\n경험 있으신 분들 조언 부탁드려요.', '2024-12-06T09:45:30Z'),
  (3006, '리프팅 시술 후기 궁금해요', '시술 고민', '인모드 리프팅 받으려고 하는데\n실제 효과가 어떤지 궁금해요.\n\n통증이나 다운타임은 어떤가요?\n후기 많이 올려주시면 감사하겠습니다!', '2024-12-05T11:20:15Z'),
  (3007, '기미 레이저 토닝 효과 있을까요?', '피부 고민', '기미 때문에 고민이에요.\n레이저 토닝으로 개선 가능한지\n궁금합니다.\n\n여러 번 받아야 효과가 있는 건가요?\n경험 있으신 분들 조언 부탁드려요.', '2024-12-04T13:10:40Z'),
  (3008, '지방분해주사 효과 궁금해요', '시술 고민', '복부 지방 때문에 고민이에요.\n지방분해주사 효과가 정말 있는지\n궁금합니다.\n\n운동과 병행하면 더 효과가 좋은가요?\n후기 부탁드려요!', '2024-12-03T15:30:25Z'),
  (3009, '통역 서비스 있는 병원 추천해주세요', '병원 선택', '한국어가 서툴러서\n통역 서비스 있는 병원 찾고 있어요.\n\n강남 지역에서\n통역 잘 해주는 곳 추천 부탁드려요!', '2024-12-02T10:45:15Z'),
  (3010, '시술 후 회복 기간 어떻게 보내셨나요?', '시술 고민', '첫 수술이라 회복 기간이 걱정이에요.\n\n일상생활은 언제쯤 가능한가요?\n회복 기간 동안 주의사항이나\n팁 있으시면 공유해주세요!', '2024-12-01T12:15:30Z'),
  (31234, '무절개 쌍꺼풀 괜찮을까요?', '시술 고민', '무절개 쌍꺼풀 받으려고 하는데\n유지 기간이 걱정이에요.\n\n절개 수술과 비교해서\n어떤 게 나을까요?\n경험 있으신 분들 조언 부탁드려요!', '2024-11-30T09:20:15Z'),
  (32345, '상안검 수술 후기 궁금해요', '시술 고민', '상안검 수술 받으려고 하는데\n실제 효과가 어떤지 궁금해요.\n\n회복 기간이나 다운타임은 어떤가요?\n후기 많이 올려주시면 감사하겠습니다!', '2024-11-29T11:30:40Z'),
  (33456, '콧볼 축소 수술 고민이에요', '시술 고민', '콧볼이 넓어서 고민이에요.\n수술 받을지 고민 중인데\n회복 기간이 걱정이에요.\n\n경험 있으신 분들 조언 부탁드려요!', '2024-11-28T13:45:25Z'),
  (34567, '사각턱 보톡스 효과 궁금해요', '시술 고민', '사각턱 때문에 고민이에요.\n보톡스로 개선 가능한지\n궁금합니다.\n\n여러 번 받아야 효과가 있는 건가요?\n후기 부탁드려요!', '2024-11-27T10:15:30Z'),
  (35678, '울쎄라 리프팅 통증 궁금해요', '시술 고민', '울쎄라 리프팅 받으려고 하는데\n통증이 걱정이에요.\n\n실제로 얼마나 아픈가요?\n경험 있으신 분들 조언 부탁드려요!', '2024-11-26T14:20:45Z'),
  (36789, '복부 지방흡입 후기 궁금해요', '시술 고민', '복부 지방흡입 받으려고 하는데\n실제 효과가 어떤지 궁금해요.\n\n회복 기간이나 다운타임은 어떤가요?\n후기 많이 올려주시면 감사하겠습니다!', '2024-11-25T09:30:20Z'),
  (37890, '피코레이저 효과 있을까요?', '피부 고민', '모공과 잡티 때문에 고민이에요.\n피코레이저로 개선 가능한지\n궁금합니다.\n\n여러 번 받아야 효과가 있는 건가요?\n경험 있으신 분들 조언 부탁드려요.', '2024-11-24T12:40:15Z'),
  (38901, '강남 지역 병원 추천 부탁드려요', '병원 선택', '강남 지역에서 시술 받을 병원 찾고 있어요.\n여러 곳 상담 다녀봤는데\n결정이 어렵네요.\n\n추천해주실 곳 있나요?', '2024-11-23T15:25:30Z'),
  (39012, '시술 비용 너무 비싼가요?', '시술 고민', '여러 병원 상담 받아봤는데\n가격 차이가 너무 커서\n헷갈려요.\n\n적정 가격대가 어느 정도인지\n알고 싶어요.', '2024-11-22T11:15:40Z');
